<?php@session_start();function isAdministrativeUser() {    if (isset($_SESSION['Authenticity']) &&            ($_SESSION['Authenticity'] == TRUE) &&            ($_SESSION['Position'] == "Administrator"))        return true;    else        return false;}function isNormalUser() {    if (isset($_SESSION['Authenticity']) &&            ($_SESSION['Authenticity'] == TRUE) &&            ($_SESSION['Position'] == "Client"))        return true;    else        return false;}function isUnloggedUser() {    if (!isset($_SESSION['Authenticity']) &&            ($_SESSION['Authenticity'] != TRUE) &&            ($_SESSION['Position'] != "Administrator"))        return true;    else        return false;}function ForceAdministrativePage() {    if (!isAdministrativeUser()) {        header("Content-Type: text/html; charset=utf-8");        header("Refresh: 5; url=../index.php?page=login");        die("Warning your ip has been logged");#hehe    }}function force_unlogged_user() {    if (isNormalUser() || isAdministrativeUser()) {        header("Location: /index.php");    }}?>